package org.fleetwave.app.notif; public final class EmailMessage{ private final String from,to,subject,html,text,replyTo; public EmailMessage(String f,String t,String s,String h,String x,String r){from=f;to=t;subject=s;html=h;text=x;replyTo=r;} public String getFrom(){return from;} public String getTo(){return to;} public String getSubject(){return subject;} public String getHtml(){return html;} public String getText(){return text;} public String getReplyTo(){return replyTo;} }