package org.fleetwave.web.api; import org.fleetwave.domain.Alert; import org.fleetwave.domain.repo.AlertRepository; import org.springframework.web.bind.annotation.*; import java.util.*; @RestController @RequestMapping("/api/alerts") public class AlertController{ private final AlertRepository repo; public AlertController(AlertRepository r){ repo=r;} @GetMapping public List<Alert> list(){ return repo.findAll(); } }