package org.fleetwave.web.api; import org.fleetwave.domain.*; import org.fleetwave.domain.repo.*; import org.springframework.web.bind.annotation.*; import java.util.*; @RestController @RequestMapping("/api/radios") public class RadioController{ private final RadioRepository radios; public RadioController(RadioRepository r){radios=r;} @GetMapping public List<Radio> list(){ return radios.findAll(); } @PostMapping public Radio create(@RequestBody Radio radio){ if(radio.getId()==null) radio.setId(java.util.UUID.randomUUID()); return radios.save(radio);} }